                    </motion.div>

                    {/* Right Content - Enhanced Visual */}
                    <motion.div
                        className="mt-8 md:mt-0 text-center md:text-left relative"
                        initial={{ opacity: 0, x: 30 }}
                        animate={{ opacity: 1, x: 0 }}
                        transition={{ duration: 0.7, delay: 0.4, ease: "easeOut" }}
